mapper.update:
UPDATE APPROVAL_LINE

	
		STATUS = #{status},
	
	
		REJECTION = #{rejection},
	
	APPROVAL_DATE = CURRENT_DATE

WHERE APPROVAL_ID = #{approval_id}
AND RECIPIENT_ID = #{recipient_id}

mapper.select:
SELECT STEP
FROM APPROVAL_LINE al
WHERE RECIPIENT_ID = #{recipient_id}
AND APPROVAL_ID = #{approval_id}
